const node_openssl = require('../index.js');
var fs = require('fs');

var options = {
	//binpath: 'C:/Program Files/OpenVPN/bin/openssl.exe'
	binpath: 'C:/Program Files/OpenSSL-Win64/bin/openssl.exe'
}

var openssl = new node_openssl(options);

fs.readFile('./CYOPKI_LE_Test/cyopki.cer', function(err, cert) {
	fs.readFile('./CYOPKI_LE_Test/intermediate.txt', function(err, chain) {
		openssl.verifyChain(cert.toString(), chain.toString(), function(err, out, cmd) {
			if(err) {
				console.log(err);
			} else {
				console.log(out);
				//console.log(hash);
			}
		});
	});
});